import {useState} from "react";
 
  const Inputform = () => {
    
    const [inputValue, setInputValue] = useState('')
    return <form>
      <input type="text"
       onChange={(e) => {
        setInputValue(e.target.value);
        // console.log({val: e.target.value})
      }}
      value = {inputValue}
      />
      <div>{inputValue}</div>
      <input type='submit'/>
    </form>
  }

  export {Inputform};
