import {useState} from "react";
import { InputForm } from './InputForm';

export default function ReactComp() {
  const [count, setCount] = useState(0);
  const addCount = (e) => {
    setCount(count +1)
  }
  const subCount = (e) => {
    setCount(count - 1)
  }

  return (
     <div id="box">
      <button id="add" onClick={addCount}>Add</button>
      <span>count:{count}</span>
       <button id="sub" onClick={subCount}>Sub</button>
       <InputForm rcCount={count}>
        <Comp1>campCount={count*1000}</Comp1> 
        <Comp1>campCount = {count*3000}</Comp1> 
        <div>Hello from div</div>
       </InputForm>
     </div>
  );
}
const Comp1 = (props) => {
  const {compCount} = props;
  return <div>Form Comp1: {compCount}</div>;
};
